package SyntaxAndDatatypes;

public class PrimitiveDatatypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int roll = 24;
		char sec = 'B';
		float marks = 92.37f;
		long phoneN = 83876328693323l;
		double BMI = 23456.788920364;
		
		//String is a non-primitive one
		String name = "Aditya Kaushal";
		
		System.out.println("Name = "+name+"\nRoll = "+roll+"\nSection = "+sec+"\nMobile No = "+phoneN+"\nMarks = "+marks+"\nBMI = "+BMI);
		
	}

}
